import 'package:bloc/bloc.dart';import 'package:flutter/material.dart';part 'counter_event.dart';part 'counter_state.dart';class CounterNewsBloc extends CounterBloc {}class CounterBlogBloc extends CounterBloc {}class CounterBloc extends Bloc<CounterEvent, CounterState> {  CounterBloc() : super(const CounterInitialState(counter: 0)) {    on<CounterIncreaseEvent>(_onCounterIncreaseEvent);  }  void _onCounterIncreaseEvent(    CounterIncreaseEvent event,    Emitter<CounterState> emit,  ) {    emit(CounterUpdatedState(counter: state.counter + 1));  }}